import React from 'react';
import { Chart } from "react-google-charts";
import Plot from 'react-plotly.js';


export function MyChart(props) {
  console.log(props.ar)
  const trace1 = {
    x: props.xValues,
    y: props.yValues1,
    mode: 'lines+markers',
    name: 'Curve 1',
    line: {
      color: 'blue',
      width: 2,
    },
  };

  const trace2 = {
    x: props.xValues,
    y: props.yValues2,
    mode: 'lines+markers',
    name: 'Curve 2',
    line: {
      color: 'red',
      width: 2,
    },
  };

  const data = [trace1, trace2];

  const layout = {
    title: 'Multiple Curves in Single Graph',
    xaxis: {
      title: 'X Axis Label',
    },
    yaxis: {
      title: 'Y Axis Label',
    },
  };

  return (
    <Plot
      data={data}
      layout={layout}
      style={{ width: '100%', height: '100%' }}
    />
  );
}






export function MyChart1(props) {
  var a = props.a || []; // add a check to assign an empty array if 'a' is undefined or null
  var ar = [["Water Content (%)","Angle of Internal Friction"]];
  for(let i = 0; i < a.length; i++){ // use '<' instead of '<=' to avoid accessing undefined index
    ar.push([a[i], props.b[i]]);
  }
  const data = [{    x: a,    y: props.b,    type: 'scatter',    mode: 'lines+markers',line: {
    shape: 'spline',
    color: 'rgb(82, 179, 201)',
    smoothing: 1.3,
    width: 3,
  },  }];
  const layout = {

    xaxis: {
      title: ar[0][0],
      tickmode: 'linear',
    dtick: 2,

    },
    yaxis: {
      title: ar[0][1],
      tickmode: 'linear',
    dtick: 2,
    },
    //plot_bgcolor: 'white',
  //paper_bgcolor: 'green',
  };

  return (
    <Plot
      data={data}
      layout={layout}
      style={{ width: "90%", height: "600px",display: 'flex', justifyContent: 'center', margin: 'auto' }}
    />
  );
}




export function MyChart2  (props) {
  console.log(props.sieve_size)
  const data = [    {      x:props.sieve_size,
    y: props.cumulative_percent_passing,
    type: 'scatter',
    mode: 'markers+lines',
    marker: {
      color: '#1f77b4',
      size: 5
    },
    line: {
      shape: 'spline'
    }
  },
];

const layout = {

  xaxis: {
    title: {
      text: 'Particle Size (mm)',
      font: {
        size: 12
      }
    },
    type: 'log',
    autorange: 'reversed'
  },
  yaxis: {
    title: {
      text: 'Cumulative Percentage Passing (%)',
      font: {
        size: 12
      }
    },
    tickfont: {
      size: 14
    }
  },
  plot_bgcolor: '#f8f8f8',
  margin: {
    l: 60,
    r: 20,
    t: 80,
    b: 60
  },
   // increase width to 800 pixels
  animate: {
    duration: 1000,
    easing: 'cubic-in-out'
  }
};

  return (
    <div style={{ display: 'flex', justifyContent: 'center', margin: 'auto', width: '100%',paddingBottom:'40px' }} className="container">
      <Plot data={data} layout={layout} />
    </div>

  );
};


export default (MyChart,MyChart1,MyChart2);
