import React, { useEffect } from 'react';
import { Chart } from "react-google-charts";



export function MyChart(props) {
    console.log(props.ar)

    const data = props.ar;
   const options = {
    title: 'Compaction Test Result of Ferro Manganese Slag',
      hAxis: {
        title: (props.ar[0][0]+" (%)"),
      },
      vAxis: {
        title: "Density (g/cm3)",
      },
      series: {

        0: { curveType: "function" },
        1: { curveType: "function" },
        2: { curveType: "function" },
      },
    };



    return (
      <>

      <Chart
        chartType="LineChart"
        width="100%"
        height="600px"
        data={data}
        options={options}
      />







      </>)
}




export function MyChart1(props) {
  var a = props.a || []; // add a check to assign an empty array if 'a' is undefined or null
  var ar = [["Water Content (%)","Angle of Internal Friction"]];
  for(let i = 0; i < a.length; i++){ // use '<' instead of '<=' to avoid accessing undefined index
    ar.push([a[i], props.b[i]]);
  }
  const data = ar;
 const options = {
  title: 'Direct Shear Test Result of Ferro Manganese Slag',
    hAxis: {
      title: (ar[0][0]),
    },
    vAxis: {
      title: ar[0][1],
    },
    series: {

      0: { curveType: "function" },
      1: { curveType: "function" },

    },
  };



  return (
    <>

    <Chart
      chartType="LineChart"
      width="100%"
      height="600px"
      data={data}
      options={options}
    />







    </>)
}







export function MyChart2  () {

  const data = [
    ["Particle Size (mm)", "Cumulative Percent Passing (%)"],
    [10, 100],
    [4.75, 86.96],
    [2.36, 72.48],
    [1.18, 44.29],
    [0.6, 20.9],
    [0.3, 10.18],
    [0.15, 4.56],
    [0.075, 2.1],
  ];

  const options = {
    title: "Sieve Analysis Test Result of Silico Manganese Slag",
    hAxis: {
      title: "Particle Size (mm)",
      logScale: false,
    },
    vAxis: {
      title: "Cumulative Percent Passing (%)",
      logScale: true,
      format: "#'%'",
    },
    legend: {
      position: "none",
    },
    chartArea: {
      width: "80%",
      height: "80%",
    },
    series: {

      0: { curveType: "function" },},
    lineWidth: 2,
    pointSize: 0,
  };
  return (
    <Chart
    width={"100%"}
    height={"400px"}
    chartType="LineChart"
    data={data}
    options={options}
  />
  );
};



export default (MyChart,MyChart1,MyChart2);
