import React, { useEffect } from 'react';
import { Chart } from "react-google-charts";
import Plot from 'react-plotly.js';


export function MyChart(props) {
    console.log(props.ar)

    const data = props.ar;
   const options = {
    title: 'Compaction Test Result of Ferro Manganese Slag',
      hAxis: {
        title: (props.ar[0][0]+" (%)"),
      },
      vAxis: {
        title: "Density (g/cm3)",
      },
      series: {

        0: { curveType: "function" },
        1: { curveType: "function" },
        2: { curveType: "function" },
      },
    };



    return (
      <>

      <Chart
        chartType="LineChart"
        width="100%"
        height="600px"
        data={data}
        options={options}
      />







      </>)
}




export function MyChart1(props) {
  var a = props.a || []; // add a check to assign an empty array if 'a' is undefined or null
  var ar = [["Water Content (%)","Angle of Internal Friction"]];
  for(let i = 0; i < a.length; i++){ // use '<' instead of '<=' to avoid accessing undefined index
    ar.push([a[i], props.b[i]]);
  }
  const data = ar;
 const options = {
  title: 'Direct Shear Test Result of Ferro Manganese Slag',
    hAxis: {
      title: (ar[0][0]),
    },
    vAxis: {
      title: ar[0][1],
    },
    series: {

      0: { curveType: "function" },
      1: { curveType: "function" },

    },
  };



  return (
    <>

    <Chart
      chartType="LineChart"
      width="100%"
      height="600px"
      data={data}
      options={options}
    />







    </>)
}







class MyChart2 extends Component {
  render() {
    const data = [
      {
        x: [1, 2, 3, 4, 5],
        y: [10, 100, 1000, 10000, 100000],
        type: 'scatter',
      },
    ];

    const layout = {
      title: 'Semi-Logarithmic Graph',
      xaxis: { title: 'X-Axis' },
      yaxis: { title: 'Y-Axis (logarithmic scale)', type: 'log' },
    };

    return (
      <div>
        <Plot data={data} layout={layout} />
      </div>
    );
  }
}


export default (MyChart,MyChart1,MyChart2);
