import React, { useEffect } from 'react';
import { Chart } from "react-google-charts";



export function MyChart(props) {
    console.log(props.ar)

    const data = props.ar;
   const options = {
    title: 'Compaction Test Result of Ferro Manganese Slag',
      hAxis: {
        title: (props.ar[0][0]+" (%)"),
      },
      vAxis: {
        title: "Density (g/cm3)",
      },
      series: {

        0: { curveType: "function" },
        1: { curveType: "function" },
        2: { curveType: "function" },
      },
    };



    return (
      <>

      <Chart
        chartType="LineChart"
        width="100%"
        height="600px"
        data={data}
        options={options}
      />







      </>)
}




export function MyChart1(props) {
  var a = props.a || []; // add a check to assign an empty array if 'a' is undefined or null
  var ar = [["Water Content (%)","Angle of Internal Friction"]];
  for(let i = 0; i < a.length; i++){ // use '<' instead of '<=' to avoid accessing undefined index
    ar.push([a[i], props.b[i]]);
  }
  const data = ar;
 const options = {
  title: 'Direct Shear Test Result of Ferro Manganese Slag',
    hAxis: {
      title: (ar[0][0]),
    },
    vAxis: {
      title: ar[0][1],
    },
    series: {

      0: { curveType: "function" },
      1: { curveType: "function" },

    },
  };



  return (
    <>

    <Chart
      chartType="LineChart"
      width="100%"
      height="600px"
      data={data}
      options={options}
    />







    </>)
}







export function MyChart2  () {

  useEffect(() => {

    const sieve_size = [10,4.75,2.36,1.18,0.6,0.3,0.15,0.075]; // in mm
    const weight_retained = [4, 18.5, 71, 154.5, 155.9, 60.5, 20.0, 15.6];
    const cumulative_percent_passing = [];
    const cumulative_percent_retained = [];

    let a=0;
    let total_wt=0;
    for (let i=0; i<sieve_size.length; i++){
      total_wt += weight_retained[i];
    }
    a = weight_retained[0] / total_wt * 100;
    cumulative_percent_retained.push(a.toFixed(2));
    cumulative_percent_passing.push((100-a).toFixed(2));
    for (let i=1; i<sieve_size.length; i++){
      a = a + weight_retained[i] / total_wt * 100;
      cumulative_percent_retained.push(a.toFixed(2));
      cumulative_percent_passing.push((100-a).toFixed(2));
    }

    const sieveData = {
      labels: [...sieve_size],
      datasets: [{
        label: "Cumulative Percent Passing",
        data: [...cumulative_percent_passing],
        type: "line",
        pointRadius: 0,
        fill: false,
        lineTension: 0.4,
        borderWidth: 2,
        borderColor: "red"
      }]
    };

    const ctx = document.getElementById("myCanvas").getContext("2d");
    new Chart(ctx, {
      type: "line",
      data: sieveData,
      options: {
        scales: {
          xAxes: [{
            scaleLabel: {
              display: true,
              labelString: "Particle Size (mm)"
            }
          }],
          yAxes: [{
            type: "logarithmic",
            position: "left",
            scaleLabel: {
              display: true,
              labelString: "Cumulative Percent Passing (%)"
            },
            ticks: {
              min: 0,
              max: 100,
              stepSize: 10,
              callback: function(value, index, values) {
                return value + "%";
              }
            }
          }]
        },
        plugins: {
          datalabels: {
            formatter: function(value, ctx) {
              return value + "%";
            }
          }
        }
      }
    });

  }, []);

  return (
    <div style={{ width: "100%", marginTop: "50px" }}>
      <canvas id="myCanvas"></canvas>
    </div>
  );
};



export default (MyChart,MyChart1,MyChart2);
