import React, { useEffect } from 'react';
import { Chart } from "react-google-charts";
import Plot from 'react-plotly.js';


export function MyChart(props) {
    console.log(props.ar)

    const data = props.ar;
   const options = {
    title: 'Compaction Test Result of Ferro Manganese Slag',
      hAxis: {
        title: (props.ar[0][0]+" (%)"),
      },
      vAxis: {
        title: "Density (g/cm3)",
      },
      series: {

        0: { curveType: "function" },
        1: { curveType: "function" },
        2: { curveType: "function" },
      },
    };



    return (
      <>

      <Chart
        chartType="LineChart"
        width="100%"
        height="600px"
        data={data}
        options={options}
      />







      </>)
}




export function MyChart1(props) {
  var a = props.a || []; // add a check to assign an empty array if 'a' is undefined or null
  var ar = [["Water Content (%)","Angle of Internal Friction"]];
  for(let i = 0; i < a.length; i++){ // use '<' instead of '<=' to avoid accessing undefined index
    ar.push([a[i], props.b[i]]);
  }
  const data = ar;
 const options = {
  title: 'Direct Shear Test Result of Ferro Manganese Slag',
    hAxis: {
      title: (ar[0][0]),
    },
    vAxis: {
      title: ar[0][1],
    },
    series: {

      0: { curveType: "function" },
      1: { curveType: "function" },

    },
  };



  return (
    <>

    <Chart
      chartType="LineChart"
      width="100%"
      height="600px"
      data={data}
      options={options}
    />







    </>)
}





export function MyChart2  () {

  const data = [    {      x:[10,4.75,2.36,1.18,0.6,0.3,0.15,0.075],
    y: [99.20,95.50,81.30,50.40,19.22,7.12,3.12,0.00],
    type: 'scatter',
    mode: 'markers+lines',
    marker: {
      color: '#1f77b4',
      size: 5
    },
    line: {
      shape: 'spline'
    }
  },
];

const layout = {
  title: {
    text: 'Semi-Logarithmic Graph',
    font: {
      size: 20
    }
  },
  xaxis: {
    title: {
      text: 'Particle Size (mm)',
      font: {
        size: 16
      }
    },
    type: 'log',
    autorange: 'reversed'
  },
  yaxis: {
    title: {
      text: 'Cumulative Percentage Passing (%)',
      font: {
        size: 16
      }
    },
    tickfont: {
      size: 14
    }
  },
  plot_bgcolor: '#f8f8f8',
  margin: {
    l: 60,
    r: 20,
    t: 80,
    b: 60
  },
   // increase width to 800 pixels
  animate: {
    duration: 1000,
    easing: 'cubic-in-out'
  }
};

  return (
    <div style={{ display: 'flex', justifyContent: 'center', margin: 'auto', width: '100%',paddingBottom:'310px' }} className="container">
      <Plot data={data} layout={layout} />
    </div>

  );
};


export default (MyChart,MyChart1,MyChart2);
